class User{constructor(t,s){this.name=t,this.lastname=s}get fullName(){return`${this.name} ${this.lastname}`}set fullName(t){[this.name,this.lastname]=t.split(" ")}}const user=new User("Jane","Doe");console.log(user.fullName),user.fullName="Alice Wood",console.log(user.fullName);class Point{constructor(t,s){this.x=t,this.y=s}printPosition(){console.log(`Current point position X: ${this.x}, Y: ${this.y}`)}}class Point3D extends Point{constructor(t,s,o){super(t,s),this.z=o}printPosition(){super.printPosition(),console.log(`Current point position X: ${this.x}, Y: ${this.y}, Z: ${this.z}`)}}const point=new Point3D(10,-2,5);point.printPosition();